table.SQL_LOAD_EDIT:
set nocount on
declare @GroupRole nvarchar(max) set @GroupRole = '' select @GroupRole = @GroupRole + ';' + convert(nvarchar(10), GroupRole_RoleID) from wcms_GroupRole where GroupRole_GroupID = @editID if (@GroupRole <> '') set @GroupRole = substring(@GroupRole, 2, len(@GroupRole) - 1)
select [Group_ID] ,[Group_Name] ,@GroupRole as [Group_GroupRole] ,[Group_O] ,[Group_A] ,[Group_R] from wcms_Group where Group_ID = @editID { and (Group_R is null OR Group_R @SA) }
