table.SQL_ADD:

set nocount on
insert wcms_IP
(
	[IP_IP]
	,[IP_Date]
	,[IP_Note]
	,[IP_A]
) 
values
(
	 {@Get:IP_IP}
	,{@Get:IP_Date}
	,{@Get:IP_Note}
	,{@Get:IP_A}
)

select SCOPE_IDENTITY()

table.SQL_UPDATE:

set nocount on

update wcms_IP set
	   [IP_IP]		= {@Get:IP_IP}
	  ,[IP_Date]	= {@Get:IP_Date}		  
	  ,[IP_Note]	= {@Get:IP_Note}
	  ,[IP_A]		= {@Get:IP_A}
where IP_ID = @editID

select @editID
