table.SQL_ADD:

set nocount on
declare @id int
set @id = {@Get:Module_ID}

insert wcms_Module
(
	[Module_ID]
	,[Module_ParentID]
	,[Module_Name]
	,[Module_Path]
	,[Module_ForAdmin]
	,[Module_EditHtml]
	,[Module_IsHeader]
	,[Module_A]
) 
values
(
	@id
	,{@Get:Module_ParentID}
	,{@Get:Module_Name}
	,{@Get:Module_Path}
	,{@Get:Module_ForAdmin}
	,{@Get:Module_EditHtml}
	,{@Get:Module_IsHeader}
	,{@Get:Module_A}
)

select @id

table.SQL_UPDATE:

set nocount on
declare @gid int = {@GID}
if (@gid <> 1)
BEGIN
	select 0
	return;
END

update wcms_Module set
		[Module_ParentID]	= {@Get:Module_ParentID}
		,[Module_Name]		= {@Get:Module_Name}
		,[Module_Path]		= {@Get:Module_Path}
		,[Module_ForAdmin]	= {@Get:Module_ForAdmin}
		,[Module_EditHtml]	= {@Get:Module_EditHtml}
		,[Module_IsHeader]	= {@Get:Module_IsHeader}
		,[Module_A]			= {@Get:Module_A}
where Module_ID = @editID

select @editID
