table.SQL_ADD:

set nocount on
insert wcms_Url
(
	Url_Source
	,Url_Destination
	,Url_PageID
	,Url_SiteID
	,Url_ItemID
	,Url_Date
	,Url_A
) 
values
(
	 {@Get:Url_Source}
	,{@Get:Url_Destination}
	,{@Get:Url_PageID}
	,{@Get:Url_SiteID}
	,{@Get:Url_ItemID}
	,getutcdate()
	,{@Get:Url_A}
)

select SCOPE_IDENTITY()

table.SQL_UPDATE:

set nocount on

update wcms_Url set
	[Url_Source]		= {@Get:Url_Source}
	,[Url_Destination]	= {@Get:Url_Destination}
	,[Url_PageID]		= {@Get:Url_PageID}
	,[Url_SiteID]		= {@Get:Url_SiteID}
	,[Url_ItemID]		= {@Get:Url_ItemID}
	,[Url_Date]			= getutcdate()
	,[Url_A]			= {@Get:Url_A}
where Url_ID = @editID

Delete from wcms_Url where Url_PageID is not null AND Url_PageID not in (Select Page_ID from wcms_Page) 

select @editID
