table.SQL_ADD:
set nocount on declare @id nvarchar(10) set @id = {@Get:Widget_ID}
insert wcms_Widget ( [Widget_ID] ,[Widget_SiteID] ,[Widget_LanguageID] ,[Widget_Type] ,[Widget_Name] ,[Widget_Code] ,[Widget_A] ) values ( cast(@id as int) ,@siteID ,@langID ,1 ,{@Get:Widget_Name} ,{@Get:Widget_Code} ,{@Get:Widget_A} )
select @id

table.SQL_UPDATE:

set nocount on
update wcms_Widget set
		[Widget_Name]			= {@Get:Widget_Name}
		,[Widget_Code] 			= {@Get:Widget_Code}
		,[Widget_A] 			= {@Get:Widget_A}
where Widget_ID = @editID

select @editID
