(function(){tinymce.create("tinymce.plugins.AdvancedImagePlugin",{init:function(n,t){n.addCommand("mceAdvImage",function(){n.dom.getAttrib(n.selection.getNode(),"class").indexOf("mceItem")==-1&&n.windowManager.open({file:t+"/image.htm",width:480+parseInt(n.getLang("advimage.delta_width",0)),height:500+parseInt(n.getLang("advimage.delta_height",0)),inline:1},{plugin_url:t})});n.addButton("image",{title:"advimage.image_desc",cmd:"mceAdvImage"})},getInfo:function(){return{longname:"Advanced image",author:"Moxiecode Systems AB",authorurl:"http://tinymce.moxiecode.com",infourl:"http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/advimage",version:tinymce.majorVersion+"."+tinymce.minorVersion}}});tinymce.PluginManager.add("advimage",tinymce.plugins.AdvancedImagePlugin)})();